import{c as n}from"./app.a4413a59.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";var s="/blog-data/assets/20201008211905736_14208.893b981f.png";const e={},o=n(`<h1 id="\u6BD4\u4F8B\u5C3A\u6A21\u5757" tabindex="-1"><a class="header-anchor" href="#\u6BD4\u4F8B\u5C3A\u6A21\u5757" aria-hidden="true">#</a> \u6BD4\u4F8B\u5C3A\u6A21\u5757</h1><p>\u5C3A\u5EA6 Scale \u662F\u4E00\u4E2A\u5C06\u62BD\u8C61\u7684\u6570\u636E\u6620\u5C04\u4E3A\u53EF\u89C6\u5316\u8868\u793A\u7684\u5DE5\u5177\uFF0C\u5177\u4F53\u800C\u8A00\u5B83\u662F\u5C06\u8F93\u5165\u57DF <code>domain</code> \u6620\u5C04\u4E3A\u8F93\u51FA\u8303\u56F4 <code>range</code> \u7684\u51FD\u6570\uFF0C\u5982\u5C06\u6D4B\u91CF\u6570\u636E\uFF08\u5355\u4F4D\u662F\u7C73\uFF09\u6620\u5C04\u4E3A\u9875\u9762\u4E0A\u7684\u70B9\uFF08\u5355\u4F4D\u662F\u50CF\u7D20\uFF09\uFF0C\u5373\u662F\u4F7F\u7528\u4F4D\u7F6E\u7F16\u7801\u5B9A\u91CF\u6570\u636E\u3002\u53EF\u4EE5\u4F7F\u7528\u4E0D\u540C\u7684\u6BD4\u4F8B\u5C3A\uFF0C\u5BF9\u4EFB\u610F\u7C7B\u578B\u7684\u6570\u636E\u8FDB\u884C\u6620\u5C04\uFF0C\u5982\u5206\u7C7B\u6570\u636E\u6216\u79BB\u6563\u7684\u6570\u636E\uFF1B\u4E5F\u53EF\u4EE5\u4F7F\u7528\u4EFB\u4F55\u89C6\u89C9\u5143\u7D20\u7F16\u7801\u6570\u636E\uFF0C\u6BD4\u5982\u989C\u8272\uFF0C\u63CF\u8FB9\u7684\u5BBD\u5EA6\u6216\u8005\u7B26\u53F7\u7684\u5927\u5C0F\u7B49\u3002</p><p>\u6784\u5EFA\u6BD4\u4F8B\u5C3A\u8FDB\u884C\u6570\u636E\u6620\u5C04\u5230\u89C6\u89C9\u5143\u7D20\u7684\u6B65\u9AA4\uFF1A</p><ol><li><code>let continuous = d3.continuous-scale</code> \u6307\u5B9A\u5408\u9002\u7684\u6BD4\u4F8B\u5C3A\u7C7B\u578B</li><li><code>continuous.domain([domain])</code> \u6307\u5B9A\u4E00\u4E2A\u6570\u503C\u6570\u7EC4\u4F5C\u4E3A\u8F93\u5165\u57DF\uFF0C\u5176\u4E2D\u6570\u7EC4\u5305\u542B\u4E24\u4E2A\u6216\u8005\u4E24\u4E2A\u4EE5\u4E0A\u5143\u7D20\uFF0C\u5982\u679C\u7ED9\u5B9A\u7684\u6570\u7EC4\u4E2D\u7684\u5143\u7D20\u4E0D\u662F\u6570\u503C\u7C7B\u578B\uFF0C\u5219\u4F1A\u88AB\u5F3A\u5236\u8F6C\u4E3A\u6570\u503C\u7C7B\u578B\u3002\u{1F4A1} \u5C3D\u7BA1\u5BF9\u4E8E\u8FDE\u7EED\u6BD4\u4F8B\u5C3A\u6765\u8BF4\uFF0C<code>domain</code> \u901A\u5E38\u5305\u542B\u4E24\u4E2A\u503C\u5C31\u53EF\u4EE5\uFF0C\u4F46\u662F\u6307\u5B9A\u591A\u4E2A\u503C\u7684\u8BDD\u4F1A\u751F\u6210\u4E00\u4E2A\u5206\u6BB5\u7684\u6BD4\u4F8B\u5C3A\u3002</li><li><code>continuous.range([range])</code> \u6307\u5B9A\u4E00\u4E2A\u6570\u503C\u6570\u7EC4\u4F5C\u4E3A\u8F93\u51FA\u7684\u8303\u56F4\uFF0C\u7C7B\u4F3C\u4E8E <code>domain</code>\uFF0C\u4F46\u5143\u7D20\u4E0D\u5FC5\u662F\u6570\u503C\u7C7B\u578B\uFF08\u5982\u679C\u9700\u8981\u4F7F\u7528 <code>continuous.invert(value)</code> \u5B9E\u73B0\u53CD\u5411\u6620\u5C04\u9664\u5916\uFF09</li></ol><p>\u{1F4A1} \u5728\u6620\u5C04\u5173\u7CFB\u4E2D\uFF0C\u4F7F\u7528 <strong><code>domain</code></strong> \u9650\u5B9A<strong>\u8F93\u5165</strong>\u57DF\u8303\u56F4\uFF0C\u4F7F\u7528 <strong><code>range</code></strong> \u9650\u5B9A<strong>\u8F93\u51FA</strong>\u7684\u8303\u56F4\uFF0C\u7136\u540E\u518D\u6839\u636E\u9009\u62E9\u7684\u6BD4\u4F8B\u5C3A\u7C7B\u522B\uFF0CD3 \u5C31\u53EF\u4EE5\u81EA\u52A8\u8BA1\u7B97\u51FA\u5E38\u89C1\u7684\u6620\u5C04\u89C4\u5219\u3002</p><p><strong>\u8FDE\u7EED\u6BD4\u4F8B\u5C3A <a href="https://d3js.org.cn/document/d3-scale/#continuous-scales" target="_blank" rel="noopener noreferrer">Continuous Scales</a></strong>\uFF1A\u5BF9\u4E8E\u8FDE\u7EED\u7684\u5B9A\u91CF\u6570\u636E\u8FDB\u884C\u6620\u5C04\uFF0C\u5982\u5C06\u6D4B\u91CF\u5355\u4F4D\u7C73\u4F7F\u7528\u50CF\u7D20\u6620\u5C04\uFF0C\u5373\u4F7F\u7528\u4F4D\u7F6E\u7F16\u7801\u5B9A\u91CF\u6570\u636E</p><p><strong>\u5E8F\u6570\u6BD4\u4F8B\u5C3A <a href="https://github.com/d3/d3-scale/tree/v3.2.2#ordinal-scales" target="_blank" rel="noopener noreferrer">Ordinal Scales</a></strong>\uFF1A\u5BF9\u4E8E\u79BB\u6563\u7684\u987A\u5E8F\uFF08\u6709\u5E8F\uFF09\u6216\u8005\u5206\u7C7B\uFF08\u65E0\u5E8F\uFF09\u6570\u636E\u8FDB\u884C\u6620\u5C04\uFF0C\u6307\u5B9A\u4ECE\u4E00\u7EC4\u6570\u636E\u503C\u5230\u4E00\u7EC4\u76F8\u5E94\u89C6\u89C9\u5C5E\u6027\u7684\u663E\u5F0F\u6620\u5C04\uFF0C\u5982\u4F7F\u7528\u989C\u8272\u89C6\u89C9\u5143\u7D20\u7F16\u7801\u79BB\u6563\u6570\u636E\u3002</p><p>\u{1F4A1} \u5176\u4ED6\u6BD4\u4F8B\u5C3A\uFF1A</p><ul><li><a href="https://d3js.org.cn/document/d3-scale/#quantize-scales" target="_blank" rel="noopener noreferrer">Quantize scale</a> \u91CF\u5316\u6BD4\u4F8B\u5C3A\uFF0C\u4E0E\u7EBF\u6027\u6BD4\u4F8B\u5C3A linear scales \u7C7B\u4F3C\uFF0C\u4F46\u662F\u5176\u8F93\u51FA\u533A\u95F4\u662F\u79BB\u6563\u7684\u800C\u4E0D\u662F\u8FDE\u7EED\u7684\u3002\u5C06\u8FDE\u7EED\u6570\u636E\u56DB\u820D\u4E94\u5165\u5230\u4E00\u7EC4\u56FA\u5B9A\u7684\u79BB\u6563\u503C\u4E2D\uFF0C\u53EF\u4EE5\u7528\u6765\u751F\u6210\u79BB\u6563\u6570\u636E\u3002</li><li><a href="https://d3js.org.cn/document/d3-scale/#quantile-scales" target="_blank" rel="noopener noreferrer">Quantile scale</a> \u5206\u4F4D\u6570\u6BD4\u4F8B\u5C3A\uFF0C\u4ECE\u6837\u672C\u603B\u4F53\u8BA1\u7B97\u5206\u4F4D\u6570</li><li><a href="https://d3js.org.cn/document/d3-scale/#threshold-scales" target="_blank" rel="noopener noreferrer">Threshold scale</a> \u9608\u503C\u6BD4\u4F8B\u5C3A\uFF0C\u4E0E\u91CF\u5316\u6BD4\u4F8B\u5C3A Quantize scale \u7C7B\u4F3C\uFF0C\u53EF\u4EE5\u4E3A\u4E00\u7EC4\u8FDE\u7EED\u6570\u636E\u6307\u5B9A\u5206\u5272\u9608\u503C\uFF0C\u53EA\u4E0D\u8FC7\u5B83\u4EEC\u5141\u8BB8\u5C06\u8F93\u5165\u57DF\u7684\u4EFB\u610F\u5B50\u96C6\u6620\u5C04\u5230\u8F93\u5165\u57DF\u7684\u79BB\u6563\u503C\u3002</li></ul><p>\u{1F4A1} \u8BE5\u6A21\u5757\u4E0D\u4F1A\u63D0\u4F9B\u989C\u8272\u6BD4\u4F8B\u5C3A\u6620\u5C04\u65B9\u6848\uFF0C\u53EF\u4EE5\u53C2\u8003 <a href="https://github.com/xswei/d3-scale-chromatic" target="_blank" rel="noopener noreferrer">d3-scale-chromatic</a> \u6765\u83B7\u53D6\u9700\u8981\u7684\u6570\u636E-\u989C\u8272\u6620\u5C04\u65B9\u6848\u3002</p><h2 id="\u8FDE\u7EED\u6BD4\u4F8B\u5C3A" tabindex="-1"><a class="header-anchor" href="#\u8FDE\u7EED\u6BD4\u4F8B\u5C3A" aria-hidden="true">#</a> \u8FDE\u7EED\u6BD4\u4F8B\u5C3A</h2><p>\u8FDE\u7EED\u6BD4\u4F8B\u5C3A Continuous Scales \u53EF\u4EE5\u5C06\u8FDE\u7EED\u7684\u5B9A\u91CF\u7684\u8F93\u5165\u57DF <a href="https://d3js.org.cn/document/d3-scale/#continuous_domain" target="_blank" rel="noopener noreferrer"><code>domain</code></a> \u6620\u5C04\u5230\u8FDE\u7EED\u7684\u8F93\u51FA\u8303\u56F4 <a href="https://d3js.org.cn/document/d3-scale/#continuous_range" target="_blank" rel="noopener noreferrer"><code>range</code></a>\uFF0C\u6620\u5C04\u89C4\u5219\u57FA\u4E8E\u6240\u9009\u7684\u8FDE\u7EED\u6BD4\u4F8B\u5C3A\u7C7B\u578B\u3002</p><p>\u5E38\u89C1\u7684\u8FDE\u7EED\u6BD4\u4F8B\u5C3A continuous \u7C7B\u578B\uFF1A</p><ul><li><a href="https://d3js.org.cn/document/d3-scale/#linear-scales" target="_blank" rel="noopener noreferrer"><code>d3.scaleLinear()</code></a> \u7EBF\u6027\u6BD4\u4F8B\u5C3A</li><li><a href="https://d3js.org.cn/document/d3-scale/#time-scales" target="_blank" rel="noopener noreferrer"><code>d3.scaleTime()</code></a> \u65F6\u95F4\u6BD4\u4F8B\u5C3A\uFF0C\u6620\u5C04\u65F6\u95F4\u5E8F\u5217</li><li><a href="https://d3js.org.cn/document/d3-scale/#power-scales" target="_blank" rel="noopener noreferrer"><code>scalePow()</code></a> \u5E42\u6BD4\u4F8B\u5C3A</li><li><a href="https://d3js.org.cn/document/d3-scale/#log-scales" target="_blank" rel="noopener noreferrer"><code>d3.scaleLog()</code></a> \u5BF9\u6570\u6BD4\u4F8B\u5C3A</li></ul><div class="language-javascript ext-js line-numbers-mode"><pre class="language-javascript"><code><span class="token comment">// \u6570\u503C\u95F4\u7684\u6620\u5C04</span>
<span class="token keyword">let</span> continuous <span class="token operator">=</span> d3<span class="token punctuation">.</span><span class="token function">scaleLinear</span><span class="token punctuation">(</span><span class="token punctuation">)</span>   <span class="token comment">// \u4F7F\u7528\u7EBF\u6027\u6BD4\u4F8B\u5C3A</span>
    <span class="token punctuation">.</span><span class="token function">domain</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token number">10</span><span class="token punctuation">,</span> <span class="token number">130</span><span class="token punctuation">]</span><span class="token punctuation">)</span>   <span class="token comment">// \u8BBE\u5B9A\u8F93\u5165\u57DF</span>
    <span class="token punctuation">.</span><span class="token function">range</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">960</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span>   <span class="token comment">// \u8BBE\u5B9A\u8F93\u51FA\u8303\u56F4</span>

<span class="token function">continuous</span><span class="token punctuation">(</span><span class="token number">20</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// 80</span>
<span class="token function">continuous</span><span class="token punctuation">(</span><span class="token number">50</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// 320</span>

<span class="token comment">// \u5C06\u6570\u503C\u6620\u5C04\u4E3A\u989C\u8272</span>
<span class="token keyword">let</span> color <span class="token operator">=</span> d3<span class="token punctuation">.</span><span class="token function">scaleLinear</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token function">domain</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token number">10</span><span class="token punctuation">,</span> <span class="token number">100</span><span class="token punctuation">]</span><span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token function">range</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token string">&quot;brown&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;steelblue&quot;</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token function">color</span><span class="token punctuation">(</span><span class="token number">20</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// &quot;#9a3439&quot;</span>
<span class="token function">color</span><span class="token punctuation">(</span><span class="token number">50</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// &quot;#7b5167&quot;</span>

<span class="token comment">// \u521B\u5EFA\u4E00\u4E2A diverging color scale \u5206\u6BB5\u7684\u989C\u8272\u6BD4\u4F8B\u5C3A</span>
<span class="token comment">// \u5F53\u503C\u4E3A\u8D1F\u65F6\u5728\u767D\u8272\u548C\u7EA2\u8272\u4E4B\u95F4\u63D2\u503C\uFF0C\u5F53\u503C\u4E3A\u6B63\u65F6\u5728\u767D\u8272\u548C\u7EFF\u8272\u4E4B\u95F4\u63D2\u503C</span>
<span class="token keyword">var</span> color <span class="token operator">=</span> d3<span class="token punctuation">.</span><span class="token function">scaleLinear</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token function">domain</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token function">range</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token string">&quot;red&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;white&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;green&quot;</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token function">color</span><span class="token punctuation">(</span><span class="token operator">-</span><span class="token number">0.5</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// &quot;rgb(255, 128, 128)&quot;</span>
<span class="token function">color</span><span class="token punctuation">(</span><span class="token operator">+</span><span class="token number">0.5</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// &quot;rgb(128, 192, 128)&quot;</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br></div></div><p>\u26A0\uFE0F \u5982\u679C\u7ED9\u5B9A\u7684 <code>value</code> \u4E0D\u5728 <code>domain</code> \u4E2D\uFF0C\u5E76\u4E14 <strong><a href="https://d3js.org.cn/document/d3-scale/#continuous_clamp" target="_blank" rel="noopener noreferrer"><code>clamping</code></a> \u94B3\u4F4D\u529F\u80FD</strong>\u6CA1\u6709\u542F\u7528\uFF0C\u5219\u8FD4\u56DE\u7684\u5BF9\u5E94\u7684\u503C\u4E5F\u4F1A\u4F4D\u4E8E <code>range</code> \u4E4B\u5916\uFF0C\u8FD9\u79CD\u6620\u5C04\u503C\u63A8\u7B97\u51FA\u6765\u7684\uFF1B\u5982\u679C\u542F\u7528\u4E86\u94B3\u4F4D\u529F\u80FD\uFF0C\u5219\u80FD\u4FDD\u8BC1\u8FD4\u56DE\u7684\u503C\u603B\u662F\u5904\u4E8E <code>range</code>\u3002\u94B3\u4F4D\u529F\u80FD\u5BF9\u4E8E <code>continuous.invert</code> \u4E5F\u662F\u540C\u6837\u7684\u6548\u679C\u3002</p><div class="language-javascript ext-js line-numbers-mode"><pre class="language-javascript"><code><span class="token keyword">let</span> x <span class="token operator">=</span> d3<span class="token punctuation">.</span><span class="token function">scaleLinear</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token function">domain</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token number">10</span><span class="token punctuation">,</span> <span class="token number">130</span><span class="token punctuation">]</span><span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token function">range</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">960</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token function">x</span><span class="token punctuation">(</span><span class="token operator">-</span><span class="token number">10</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// -160, outside range</span>
x<span class="token punctuation">.</span><span class="token function">invert</span><span class="token punctuation">(</span><span class="token operator">-</span><span class="token number">160</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// -10, outside domain</span>

x<span class="token punctuation">.</span><span class="token function">clamp</span><span class="token punctuation">(</span><span class="token boolean">true</span><span class="token punctuation">)</span><span class="token punctuation">;</span>   <span class="token comment">// \u5F00\u542F\u94B3\u4F4D\u529F\u80FD</span>
<span class="token function">x</span><span class="token punctuation">(</span><span class="token operator">-</span><span class="token number">10</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// 0, clamped to range</span>
x<span class="token punctuation">.</span><span class="token function">invert</span><span class="token punctuation">(</span><span class="token operator">-</span><span class="token number">160</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// 10, clamped to domain</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br></div></div><p>\u{1F4A1} \u5982\u679C\u8F93\u51FA\u8303\u56F4\u4E5F\u662F\u8FDE\u7EED\u7684\u6570\u503C\u578B\uFF0C\u5219\u53EF\u4EE5\u4F7F\u7528 <code>continuous.invert(value)</code> \u5B9E\u73B0\u53CD\u5411\u6620\u5C04\uFF0C\u5373\u6839\u636E\u76F8\u540C\u7684\u6BD4\u4F8B\u5C3A\uFF0C\u57FA\u4E8E\u7ED9\u5B9A\u7684\u4F4D\u4E8E\u8F93\u51FA\u8303\u56F4 <code>range</code> \u4E2D\u7684 <code>value</code> \u8FD4\u56DE\u5BF9\u5E94\u7684\u4F4D\u4E8E\u8F93\u5165\u57DF <code>domain</code> \u7684\u503C\u3002</p><div class="language-javascript ext-js line-numbers-mode"><pre class="language-javascript"><code><span class="token keyword">var</span> x <span class="token operator">=</span> d3<span class="token punctuation">.</span><span class="token function">scaleLinear</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token function">domain</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token number">10</span><span class="token punctuation">,</span> <span class="token number">130</span><span class="token punctuation">]</span><span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token function">range</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">960</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

x<span class="token punctuation">.</span><span class="token function">invert</span><span class="token punctuation">(</span><span class="token number">80</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// 20</span>
x<span class="token punctuation">.</span><span class="token function">invert</span><span class="token punctuation">(</span><span class="token number">320</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// 50</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><h3 id="\u7EBF\u6027\u6BD4\u4F8B\u5C3A" tabindex="-1"><a class="header-anchor" href="#\u7EBF\u6027\u6BD4\u4F8B\u5C3A" aria-hidden="true">#</a> \u7EBF\u6027\u6BD4\u4F8B\u5C3A</h3><p><code>d3.scaleLinear()</code> \u4F7F\u7528\u7EBF\u6027\u6BD4\u4F8B\u5C3A\u4F5C\u4E3A\u6620\u5C04\u89C4\u5219\u3002</p><p>\u9ED8\u8BA4\u4F7F\u7528\u5355\u4F4D <code>domain</code> <code>[0, 1]</code>, \u5355\u4F4D <code>range</code> <code>[0, 1]</code>, \u4EE5\u53CA <a href="https://github.com/xswei/d3-interpolate#interpolate" target="_blank" rel="noopener noreferrer">default</a> <a href="https://d3js.org.cn/document/d3-scale/#continuous_interpolate" target="_blank" rel="noopener noreferrer"><code>interpolator</code></a> \u548C\u5173\u95ED\u7684 <a href="https://d3js.org.cn/document/d3-scale/#continuous_clamp" target="_blank" rel="noopener noreferrer"><code>clamping</code></a> \u6784\u9020\u4E00\u4E2A\u65B0\u7684\u8FDE\u7EED\u6BD4\u4F8B\u5C3A\u3002</p><p>\u7EBF\u6027\u63D2\u503C\u5668\u662F\u4E00\u4E2A\u5F88\u597D\u7684\u9002\u7528\u4E8E\u8FDE\u7EED\u5B9A\u91CF\u6570\u636E\u7684\u6BD4\u4F8B\u5C3A\uFF0C\u56E0\u4E3A\u5B83\u5F88\u597D\u7684\u4FDD\u7559\u4E86\u6BD4\u4F8B\u5DEE\u5F02\uFF0C\u6BCF\u4E00\u4E2A <code>range</code> \u4E2D\u7684\u503C <code>y</code> \u90FD\u53EF\u4EE5\u88AB\u8868\u793A\u4E3A\u4E00\u4E2A\u51FD\u6570\uFF1A<code>y = mx + b</code>\uFF0C\u5176\u4E2D <code>x</code> \u4E3A\u5BF9\u5E94\u7684 <code>domain</code> \u4E2D\u7684\u503C\u3002</p><h2 id="\u5E8F\u6570\u6BD4\u4F8B\u5C3A" tabindex="-1"><a class="header-anchor" href="#\u5E8F\u6570\u6BD4\u4F8B\u5C3A" aria-hidden="true">#</a> \u5E8F\u6570\u6BD4\u4F8B\u5C3A</h2><p>\u4E0E\u8FDE\u7EED\u6BD4\u4F8B\u5C3A Continuous Scales \u4E0D\u540C\uFF0C\u5E8F\u6570\u6BD4\u4F8B\u5C3A Ordinal Scales \u7684\u8F93\u5165\u57DF\u548C\u8F93\u51FA\u8303\u56F4\u90FD\u662F<strong>\u79BB\u6563</strong>\u7684\uFF0C\u5982\u5C06\u4E00\u7EC4\u547D\u540D\u7C7B\u522B\u6620\u5C04\u5230\u4E00\u7EC4\u989C\u8272\uFF0C\u6216\u8005\u786E\u5B9A\u4E00\u7EC4\u6761\u5F62\u56FE\u5728\u6C34\u5E73\u65B9\u5411\u7684\u4F4D\u7F6E\u7B49\u3002</p><ul><li><code>d3.scaleOrdinal([[domain, ]range])</code> \u4F7F\u7528\u7ED9\u5B9A\u7684\u8F93\u5165\u57DF <code>domain</code> \u4E0E\u8F93\u51FA\u503C <code>range</code> \u6784\u9020\u4E00\u4E2A\u5E8F\u6570\u6BD4\u4F8B\u5C3A\u3002</li><li><code>ordinal(value)</code> \u6839\u636E\u8F93\u5165\u57DF\u4E2D\u7684\u503C <code>value</code> \u8FD4\u56DE\u5BF9\u5E94\u7684\u8F93\u5165\u57DF\u4E2D\u7684\u503C\u3002\u8F93\u5165\u57DF\u4E2D\u7684\u5143\u7D20\u6B21\u5E8F\u4E0E\u8F93\u51FA\u503C\u8303\u56F4\u4E2D\u7684\u5143\u7D20\u6B21\u5E8F\u4E00\u4E00\u5BF9\u5E94\u3002</li><li><code>ordinal.domain([domain])</code> \u5C06\u6570\u7EC4\u6307\u5B9A\u4E3A\u8F93\u5165\u57DF <code>domain</code>\uFF0C\u8F93\u5165\u57DF\u5728\u5185\u90E8\u4EE5\u5B57\u7B26\u4E32\u5230\u7D22\u5F15\u7684\u6620\u5C04 Map \u5F62\u5F0F\u5B58\u50A8\uFF0C\u7D22\u5F15\u503C\u7528\u6765\u8FDB\u884C\u8FDB\u884C\u8F93\u51FA\u68C0\u7D22\uFF0C\u56E0\u6B64\u5E8F\u6570\u6BD4\u4F8B\u5C3A\u7684\u8F93\u5165\u57DF\u7684\u503C\u5FC5\u987B\u4E3A\u5B57\u7B26\u4E32\u6216\u8005\u80FD\u88AB\u5F3A\u5236\u8F6C\u4E3A\u5B57\u7B26\u4E32\u7684\u7C7B\u578B\uFF0C\u5E76\u4E14\u5143\u7D20\u7684\u503C\u5FC5\u987B\u552F\u4E00\u3002</li><li><code>ordinal.range([range])</code> \u6307\u5B9A\u4E86 range \u5219\u5C06\u5E8F\u6570\u6BD4\u4F8B\u5C3A\u7684\u8F93\u51FA\u57DF\u8BBE\u7F6E\u4E3A\u6307\u5B9A\u7684\u6570\u7EC4\u3002\u8F93\u5165\u57DF\u4E2D\u7684\u5143\u7D20\u4E0E\u8F93\u51FA\u57DF\u4E2D\u7684\u5143\u7D20\u4E00\u4E00\u5BF9\u5E94\u3002</li></ul><h3 id="\u5206\u6BB5\u6BD4\u4F8B\u5C3A" tabindex="-1"><a class="header-anchor" href="#\u5206\u6BB5\u6BD4\u4F8B\u5C3A" aria-hidden="true">#</a> \u5206\u6BB5\u6BD4\u4F8B\u5C3A</h3><p>\u5206\u6BB5\u6BD4\u4F8B\u5C3A <a href="https://github.com/d3/d3-scale/tree/v3.2.2#band-scales" target="_blank" rel="noopener noreferrer">Band Scales</a> \u4E0E\u5E8F\u6570\u6BD4\u4F8B\u5C3A Ordinal Scales \u7C7B\u4F3C\uFF0C\u53EA\u4E0D\u8FC7\u5176\u8F93\u51FA\u57DF\u53EF\u4EE5\u662F\u8FDE\u7EED\u7684\u6570\u503C\u7C7B\u578B\uFF0C\u901A\u8FC7\u5C06\u8FDE\u7EED\u7684\u8303\u56F4\u5212\u5206\u4E3A<strong>\u5747\u5300\u7684\u5206\u6BB5</strong>\u6620\u5C04\u4E3A\u79BB\u6563\u7684\u8F93\u51FA\u503C\u3002\u8BE5\u6BD4\u4F8B\u5C3A\u4E00\u822C\u7528\u4E8E\u5305\u542B\u5E8F\u6570\u6216\u7C7B\u522B\u7EF4\u5EA6\u7684\u6761\u5F62\u56FE\uFF0C</p><p><img src="`+s+'" alt="Band Scales \u5206\u6BB5\u6BD4\u4F8B\u5C3A"></p><ul><li><code>d3.scaleBand([[domain, ]range])</code> \u4F7F\u7528\u7ED9\u5B9A\u7684\u8F93\u5165\u57DF <code>domain</code> \u4E0E\u8F93\u51FA\u503C <code>range</code> \u6784\u9020\u4E00\u4E2A\u5206\u6BB5\u6BD4\u4F8B\u5C3A\uFF0C\u9ED8\u8BA4\u4E0D\u8BBE\u7F6E <code>padding</code> \u548C <code>rounding</code>\uFF0C\u5E76\u5C45\u4E2D\u5BF9\u9F50\u3002\u{1F4A1} \u5982\u679C\u6CA1\u6709\u6307\u5B9A <code>range</code> \u5219\u9ED8\u8BA4\u4F7F\u7528\u5355\u4F4D <code>range</code> <code>[0. 1]</code></li><li><code>band(value)</code> \u6839\u636E\u8F93\u5165\u57DF\u4E2D\u7684\u503C <code>value</code> \u8FD4\u56DE\u5BF9\u5E94\u7684<strong>\u5206\u6BB5\u7684\u8D77\u70B9\u503C</strong>\uFF0C\u5982\u679C\u7ED9\u5B9A\u7684 <code>value</code> \u4E0D\u5728\u8F93\u5165\u57DF\u4E2D\u5219\u8FD4\u56DE <code>undefined</code>\u3002</li><li><code>band.domain([domain])</code> \u5C06\u6570\u7EC4\u6307\u5B9A\u4E3A\u8F93\u5165\u57DF <code>domain</code>\uFF0C\u7B2C\u4E00\u4E2A\u5143\u7D20\u5BF9\u5E94\u7B2C\u4E00\u4E2A\u5206\u6BB5\uFF0C\u7B2C\u4E8C\u4E2A\u5143\u7D20\u5BF9\u5E94\u7B2C\u4E8C\u4E2A\u5206\u6BB5\uFF0C\u4EE5\u6B64\u7C7B\u63A8\u3002\u5728\u5185\u90E8\u5206\u6BB5\u6BD4\u4F8B\u5C3A\u7684\u8F93\u5165\u4F1A\u5B58\u50A8\u5B57\u7B26\u4E32\u4E0E\u7D22\u5F15\u7684\u6620\u5C04\u5173\u7CFB\uFF0C\u56E0\u6B64\u8F93\u5165\u57DF\u4E2D\u7684\u503C\u5FC5\u987B\u662F\u5B57\u7B26\u4E32\u6216\u8005\u80FD\u88AB\u5F3A\u5236\u8F6C\u4E3A\u5B57\u7B26\u4E32\u7684\u503C\uFF0C\u5E76\u4E14<strong>\u5143\u7D20\u503C\u5FC5\u987B\u552F\u4E00</strong>\u3002</li><li><code>band.range([range])</code> \u5C06\u4E8C\u5143\u6570\u503C\u6570\u7EC4\u6307\u5B9A\u4E3A\u8F93\u51FA\u8303\u56F4 <code>range</code>\uFF0C\u5982\u679C\u6570\u7EC4\u4E2D\u5143\u7D20\u4E0D\u662F\u6570\u503C\u7C7B\u578B\u5219\u4F1A\u88AB\u5F3A\u5236\u8F6C\u4E3A\u6570\u503C\u7C7B\u578B\u3002\u5982\u679C\u6CA1\u6709\u6307\u5B9A <code>range</code> \u5219\u8FD4\u56DE\u6BD4\u4F8B\u5C3A\u5F53\u524D\u7684\u8F93\u51FA\u8303\u56F4\uFF0C\u9ED8\u8BA4\u4E3A <code>[0, 1]</code>\u3002</li><li><code>band.bandwidth()</code> \u8FD4\u56DE\u6BCF\u4E00\u4E2A\u5206\u6BB5\u7684\u5BBD\u5EA6\u3002</li><li><code>band.paddingInner([padding])</code> \u6307\u5B9A\u5206\u6BB5\u7684\u5185\u90E8\u95F4\u9694\u7684\u6BD4\u4F8B\u4E3A <code>padding</code>\uFF0C\u8303\u56F4\u5FC5\u987B\u5728 <code>[0, 1]</code> \u4E4B\u95F4\u3002\u5982\u679C\u6CA1\u6709\u6307\u5B9A <code>padding</code> \u5219\u8FD4\u56DE\u5F53\u524D\u7684\u5185\u90E8\u95F4\u9694\uFF0C\u9ED8\u8BA4\u4E3A <code>0</code>\uFF08<code>1</code> \u8868\u793A <code>bandwidth</code> \u6761\u5E26\u5BBD\u5EA6\u4E3A 0\uFF09\u3002</li><li><code>band.paddingOuter([padding])</code> \u6307\u5B9A\u7B2C\u4E00\u4E2A\u5206\u6BB5\u4E4B\u524D\u4E0E\u6700\u540E\u4E00\u4E2A\u5206\u6BB5\u4E4B\u540E\u7684\u95F4\u9694\u6BD4\u4F8B\u3002</li></ul><h3 id="\u6807\u70B9\u6BD4\u4F8B\u5C3A" tabindex="-1"><a class="header-anchor" href="#\u6807\u70B9\u6BD4\u4F8B\u5C3A" aria-hidden="true">#</a> \u6807\u70B9\u6BD4\u4F8B\u5C3A</h3><p>\u6807\u70B9\u6BD4\u4F8B\u5C3A <a href="https://github.com/d3/d3-scale/tree/v3.2.2#point-scales" target="_blank" rel="noopener noreferrer">Point Scales</a>\uFF1A\u662F Band Scales \u7684\u5206\u6BB5\u5BBD\u5EA6\u4E3A <code>0</code> \u65F6\u7684\u53D8\u4F53\uFF0C\u901A\u5E38\u7528\u4E8E\u5BF9\u5177\u6709\u5E8F\u6570\u6216\u5206\u7C7B\u7EF4\u5EA6\u7684\u6563\u70B9\u56FE\u3002</p>',32);function p(t,c){return o}var r=a(e,[["render",p]]);export{r as default};
